close all; clear all;

N = 10000;
% 减小方差：将[-0.5, 0.5]改为[-0.1, 0.1]
x = 5 + 0.2*(rand(N,1) - 0.5);  % x在[4.9, 5.1]之间均匀分布，方差更小

% 初始化
omega(1) = 10;  % 起始点改为10
g_values = zeros(N-1, 1);  % 存储每一步的g(ω)值
omega_values = zeros(N-1, 1);  % 存储每一步的ω值

% 迭代过程
for i = 2:N
    % alpha = 1/(i^0.6);
    alpha = 1/i;
    % alpha = 0.1;
    % alpha = 0.01;
    
    % 存储当前值
    omega_values(i-1) = omega(i-1);
    g_values(i-1) = omega(i-1) - x(i-1);
    
    % 更新ω
    omega(i) = omega(i-1) - alpha * g_values(i-1);
end

