grid on; %[output:6d9def10]

% 绘制几条代表性的直线 g(ω) = ω - x
% 由于x是随机变量，我们绘制x的最小值、最大值和均值的直线
omega_range = linspace(5, 10.5, 100);
x_min = min(x);
x_max = max(x);
x_mean = mean(x);

plot(omega_range, omega_range - x_min, 'b-', 'LineWidth', 1, 'DisplayName', sprintf('g(ω)=ω-%.3f (x_{min})', x_min)); %[output:6d9def10]
