plot(omega_range, omega_range - x_max, 'g-', 'LineWidth', 1, 'DisplayName', sprintf('g(ω)=ω-%.3f (x_{max})', x_max)); %[output:6d9def10]

% 绘制零线（g=0）
plot(omega_range, zeros(size(omega_range)), 'k:', 'LineWidth', 1, 'DisplayName', 'g(ω)=0'); %[output:6d9def10]

% 绘制迭代过程的点
