for i = 1:num_points_to_plot
    % 使用颜色渐变表示迭代过程
    color_ratio = i/num_points_to_plot;
    point_color = [color_ratio, 0, 1-color_ratio];  % 从蓝色渐变到红色
    
    % 绘制当前迭代点
