%%
% 随机梯度下降与批量梯度下降对比示例
close all; clear all; clc;

%% 生成数据
rng(42); % 设置随机种子以便重现结果

% 生成样本数据：y = w1*x1 + w2*x2 + 噪声
n = 100; % 样本数量
true_w1 = 2.5;
true_w2 = 1.8;

% 生成特征
x1 = 2 * randn(n, 1);  % 特征1
x2 = 1.5 * randn(n, 1); % 特征2
noise = 0.5 * randn(n, 1); % 噪声

% 生成目标值（假装为测量所得）
b = true_w1 * x1 + true_w2 * x2 + noise;

% 创建特征矩阵X（也是测量所得）
X = [x1, x2];

%% 定义损失函数和梯度
% 找一个最优的w使得X*w-b的值最小（误差最小）Z
% 最小二乘问题的梯度下降解法

%[text] $均方误差损失函数定义为：\n\\\[\nL(w) = \\frac{1}{n} \\sum\_{i=1}^{n} (y\_{\\text{pred}}^{(i)} - b\_i)^2 = \\frac{1}{n} \\|Xw - b\\|\_2^2\n\\\]\n\n向量形式表示为：\n\\\[\nL(w) = \\frac{1}{n} (Xw - b)^\\top (Xw - b)\n\\\]\n\n\\\[\nL(w) = \\frac{1}{n} (w^\\top X^\\top X w - 2w^\\top X^\\top b + b^\\top b)\n\\\]\n\n\n使用矩阵求导法则：\n1. \\(\\frac{\\partial}{\\partial w}(w^\\top X^\\top X w) = 2X^\\top X w\\)\n2. \\(\\frac{\\partial}{\\partial w}(-2w^\\top X^\\top b) = -2X^\\top b\\)\n3. \\(\\frac{\\partial}{\\partial w}(b^\\top b) = 0\\)\n\n因此：\n\\\[\n\\frac{\\partial L(w)}{\\partial w} = \\frac{1}{n} \\left( 2X^\\top X w - 2X^\\top b \\right)\n\\\]\n\n\n\n\\\[\n\\nabla\_w L(w) = \\frac{2}{n} X^\\top (X w - b)\n\\\]\n$
% 损失函数：均方误差 MSE = 1/n * Σ(y_pred - b)^2
% 梯度：∇f(w) = 2/n * X' * (X*w - b)

% 计算损失函数值的函数
compute_loss = @(w) mean((X*w - b).^2);

% 批量梯度（使用所有样本）
batch_gradient = @(w) (2/n) * X' * (X*w - b);

% 随机梯度（使用单个样本）
stochastic_gradient = @(w, idx) 2 * X(idx,:)' * (X(idx,:)*w - b(idx));

%% 参数设置
w_init = [0; 0]; % 初始参数
alpha = 0.01;    % 学习率
max_iter = 200;  % 最大迭代次数

%% 批量梯度下降 (Batch Gradient Descent)
fprintf('运行批量梯度下降...\n'); %[output:4f9d5cb5]
w_batch = w_init;
loss_batch = zeros(max_iter, 1);

for k = 1:max_iter
    % 计算梯度（使用所有样本）
    grad = batch_gradient(w_batch);
    
    % 更新参数
    w_batch = w_batch - alpha * grad;
    
    % 记录损失
    loss_batch(k) = compute_loss(w_batch);
end

%% 随机梯度下降 (Stochastic Gradient Descent)
fprintf('运行随机梯度下降...\n'); %[output:22a94299]
w_sgd = w_init;
loss_sgd = zeros(max_iter, 1);

for k = 1:max_iter
    % 随机选择一个样本
    idx = randi(n, 1);
    
    % 计算梯度（使用单个随机样本）
    grad = stochastic_gradient(w_sgd, idx);
    
    % 更新参数
    w_sgd = w_sgd - alpha * grad;
    
    % 记录损失
    loss_sgd(k) = compute_loss(w_sgd);
end

%% 创建3D可视化
fprintf('创建3D可视化...\n'); %[output:52ca8efd]

% 创建参数空间网格
w1_range = linspace(0, 4, 50);
w2_range = linspace(0, 3, 50);
[W1, W2] = meshgrid(w1_range, w2_range);

% 计算损失函数在网格上的值
Z = zeros(size(W1));
for i = 1:size(W1, 1)
    for j = 1:size(W1, 2)
        w_temp = [W1(i,j); W2(i,j)];
        Z(i,j) = compute_loss(w_temp);
    end
end

% 创建3D曲面图
figure('Position', [100, 100, 1400, 600]); %[output:106964bc]

% 子图1：3D曲面
subplot(1, 2, 1); %[output:106964bc]
surf(W1, W2, Z, 'FaceAlpha', 0.7, 'EdgeColor', 'none'); %[output:106964bc]
hold on; %[output:106964bc]
xlabel('w_1'); ylabel('w_2'); zlabel('损失函数值'); %[output:106964bc]
title('损失函数曲面与优化路径'); %[output:106964bc]
grid on; %[output:106964bc]
colormap('jet'); %[output:106964bc]

% 添加优化路径
% 为批量梯度下降路径计算损失
w1_batch_path = zeros(max_iter, 1);
w2_batch_path = zeros(max_iter, 1);
loss_batch_path = zeros(max_iter, 1);

w_batch_temp = w_init;
for k = 1:max_iter
    w1_batch_path(k) = w_batch_temp(1);
    w2_batch_path(k) = w_batch_temp(2);
    loss_batch_path(k) = compute_loss(w_batch_temp);
    
    % 更新参数（仅用于路径计算，使用相同的逻辑）
    grad = batch_gradient(w_batch_temp);
    w_batch_temp = w_batch_temp - alpha * grad;
end

% 为随机梯度下降路径计算损失
w1_sgd_path = zeros(max_iter, 1);
w2_sgd_path = zeros(max_iter, 1);
loss_sgd_path = zeros(max_iter, 1);

w_sgd_temp = w_init;
for k = 1:max_iter
    w1_sgd_path(k) = w_sgd_temp(1);
    w2_sgd_path(k) = w_sgd_temp(2);
    loss_sgd_path(k) = compute_loss(w_sgd_temp);
    
    % 更新参数（仅用于路径计算，使用相同的逻辑）
    idx = randi(n, 1);
    grad = stochastic_gradient(w_sgd_temp, idx);
    w_sgd_temp = w_sgd_temp - alpha * grad;
end

% 绘制批量梯度下降路径
plot3(w1_batch_path, w2_batch_path, loss_batch_path, 'b-', 'LineWidth', 2, 'DisplayName', '批量梯度下降'); %[output:106964bc]
plot3(w1_batch_path(1), w2_batch_path(1), loss_batch_path(1), 'bo', 'MarkerSize', 10, 'MarkerFaceColor', 'b', 'DisplayName', '起始点'); %[output:106964bc]

% 绘制随机梯度下降路径
plot3(w1_sgd_path, w2_sgd_path, loss_sgd_path, 'r-', 'LineWidth', 2, 'DisplayName', '随机梯度下降'); %[output:106964bc]
plot3(w1_sgd_path(end), w2_sgd_path(end), loss_sgd_path(end), 'rs', 'MarkerSize', 10, 'MarkerFaceColor', 'r', 'DisplayName', 'SGD终点'); %[output:106964bc]

% 标记最优解
plot3(true_w1, true_w2, 0, 'g*', 'MarkerSize', 15, 'MarkerFaceColor', 'g', 'DisplayName', '真实参数'); %[output:106964bc]
legend('Location', 'best'); %[output:106964bc]
view(-45, 30); %[output:106964bc]

% 子图2：等高线图
subplot(1, 2, 2); %[output:106964bc]
contour(W1, W2, Z, 30); %[output:106964bc]
hold on; %[output:106964bc]
xlabel('w_1'); ylabel('w_2'); %[output:106964bc]
title('损失函数等高线与优化路径'); %[output:106964bc]
grid on; %[output:106964bc]
colormap('jet'); %[output:106964bc]

% 绘制批量梯度下降路径
plot(w1_batch_path, w2_batch_path, 'b-', 'LineWidth', 2, 'DisplayName', '批量梯度下降'); %[output:106964bc]
plot(w1_batch_path(1), w2_batch_path(1), 'bo', 'MarkerSize', 10, 'MarkerFaceColor', 'b', 'DisplayName', '起始点'); %[output:106964bc]

% 绘制随机梯度下降路径
