legend('Location', 'best'); %[output:106964bc]

% 添加信息框
annotation('textbox', [0.15, 0.05, 0.3, 0.1], 'String',... %[output:group:5c58fc8f] %[output:106964bc]
    {'批量梯度下降: 每次迭代使用所有100个样本',... %[output:106964bc]
