%% main_control.m - Sarsa 接口控制演示
%% TODO: n-step Sarsa   Expected Sarsa
clear; close all; clc;

% --- 配置 ---
% x_len = 5; y_len = 5;
% start = [1, 1]; final = [3, 4];
% obs = [2,2; 3,2; 3,3; 2,4; 4,4; 2,5];

x_len = 2; y_len = 2;
start = [1, 1]; final = [2, 2];
obs = [1,2];

% 通用参数
alpha = 0.1; gamma = 0.9; epsilon = 0.5;  % 记住epsilon会影响最终收敛的状态值
episodes = 2000;
steps_per_ep = 10000; % 对于 fixed_length 模式，这个就是轨迹长度

%% === 实验 1: 模式 'reach_and_stop' (到达即停) ===
fprintf('\n=== 实验 1: Reach & Stop ===\n'); %[output:1b5e0ae1]
env1 = GridWorld(x_len, y_len, start, final, obs);
agent1 = SarsaAgent(env1.State_Space_Size, 5, alpha, gamma, epsilon, 0.998);

% 调用接口
agent1.train(env1, episodes, steps_per_ep, 'reach_and_stop'); %[output:67d7dd78]

% 结果
[V1, Pol1] = agent1.get_results();
env1.plot_values(V1);  %[output:2bd90f77]
title('Mode: Reach & Stop (Target Fixed to 10)'); %[output:2bd90f77]

%% === 实验 2: 模式 'fixed_length' (固定轨迹长度) ===
% 在这个模式下，Agent 到达终点后会继续在终点转圈(吸收态)
% 它会自然学到 V = 1 + 0.9*1 + ... = 10，而不需要强制修正
fprintf('\n=== 实验 2: Fixed Length ===\n'); %[output:2fa8e4d6]
env2 = GridWorld(x_len, y_len, start, final, obs);
agent2 = SarsaAgent(env2.State_Space_Size, 5, alpha, gamma, epsilon, 0.998);

% 调用接口
% 注意：这里 steps 设长一点，让它在终点多待一会儿，收敛更准
agent2.train(env2, episodes, 50, 'fixed_length');  %[output:58e6a835]

% 结果
[V2, Pol2] = agent2.get_results();
env2.plot_values(V2); %[output:94e802b3]
title('Mode: Fixed Length (Natural Convergence)'); %[output:94e802b3]

%% === 轨迹可视化对比 ===
% 生成一批数据来画那张壮观的图
histories = {};
test_env = GridWorld(x_len, y_len, start, final, obs);
test_agent = SarsaAgent(test_env.State_Space_Size, 5, 0.1, 0.9, 0.5, 0.995);

% 快速跑 200 个回合收集轨迹
for i=1:200
    % 使用 fixed_length 模式收集完整的 50 步轨迹
    s = test_env.coord2idx(start);
    path = [s];
    for k=1:50
        a = test_agent.choose_action(s);
        [ns, r, ~] = test_env.step(s, a);
        test_agent.update(s, a, r, ns, test_agent.choose_action(ns));
        s = ns;
        path(end+1) = s;
    end
    histories{end+1} = path;
    test_agent.decay_epsilon();
end

test_env.plot_stochastic_trajectory(histories); %[output:1d82b376]
% title('Stochastic Trajectories (Fixed Length Mode)');
%%
%% demo_spectacular.m - 展示群体探索效果
clear; close all; clc;

% 1. 初始化
x_len = 5; y_len = 5;
start = [1, 1]; final = [3, 4];
obs = [2,2; 3,2; 3,3; 2,4; 4,4; 2,5];

env = GridWorld(x_len, y_len, start, final, obs);
agent = SarsaAgent(env.State_Space_Size, 5, 0.1, 0.9, 0.5); % 初始 epsilon = 0.5 (高探索)

% 用于存储所有的路径历史
all_histories = {};

% 2. 训练并收集数据
episodes = 300; % 跑300个回合，足够画出壮观的图
fprintf('正在收集 %d 个回合的路径数据...\n', episodes);

for ep = 1:episodes
    curr_s = env.coord2idx(env.Start_State);
    curr_a = agent.choose_action(curr_s);
    
    path = [curr_s]; % 记录当前回合的路径
    
    for step = 1:100
        [next_s, r, done] = env.step(curr_s, curr_a);
        
        if done
            agent.update_terminal(curr_s, curr_a, r);
            path(end+1) = next_s;
            break;
        else
            next_a = agent.choose_action(next_s);
            agent.update(curr_s, curr_a, r, next_s, next_a);
            
            curr_s = next_s;
            curr_a = next_a;
            path(end+1) = curr_s;
        end
    end
    
    % 保存这一条路径到大集合中
    all_histories{end+1} = path;
    
    % 慢慢降低探索率，模拟从"混乱"到"有序"的过程
    agent.decay_epsilon();
end

fprintf('收集完毕，开始绘图...\n');

%% 3. 绘制壮观的聚合路径图
% 这将画出所有300条路径的叠加
% 半透明的绿色线条叠加在一起，访问越多的地方越亮（像热力图）
env.plot_stochastic_trajectory(all_histories);


