%% ========== 4网格世界MDP - 完整修正版 ==========
clear all; close all; clc;

%% 1. 定义参数
gamma = 0.9;        % 折扣因子
n_states = 4;       % 状态数量: s1, s2, s3, s4
n_actions = 5;      % 动作数量: 上(1), 下(2), 左(3), 右(4), 停(5)

%% 2. 定义转移概率和奖励（修正版）
% 初始化转移概率矩阵P和奖励矩阵R
P = zeros(n_states, n_states, n_actions);
R = zeros(n_states, n_actions, n_states);

% ========== 状态s1 (索引1) ==========
% 动作1: 上
P(1,1,1) = 0.7; R(1,1,1) = -10;  % 边界惩罚
P(1,2,1) = 0.3; R(1,1,2) = -10;  % 进入s2

% 动作2: 下
P(1,3,2) = 0.7; R(1,2,3) = 0;    % 进入s3
P(1,1,2) = 0.3; R(1,2,1) = -10;  % 边界惩罚

% 动作3: 左
P(1,1,3) = 1.0; R(1,3,1) = -10;  % 边界惩罚

% 动作4: 右
P(1,2,4) = 0.7; R(1,4,2) = -10;  % 进入s2
P(1,3,4) = 0.3; R(1,4,3) = 0;    % 进入s3

% 动作5: 停
P(1,1,5) = 1.0; R(1,5,1) = 0;    % 停留

% ========== 状态s2 (索引2) ==========
% 动作1: 上
P(2,2,1) = 1.0; R(2,1,2) = -10;  % 边界惩罚

% 动作2: 下
P(2,4,2) = 0.7; R(2,2,4) = +5;   % 进入s4
P(2,1,2) = 0.3; R(2,2,1) = 0;    % 进入s1

% 动作3: 左
P(2,1,3) = 0.7; R(2,3,1) = 0;    % 进入s1
P(2,2,3) = 0.3; R(2,3,2) = -10;  % 边界惩罚

% 动作4: 右
P(2,2,4) = 0.7; R(2,4,2) = -10;  % 边界惩罚
P(2,4,4) = 0.3; R(2,4,4) = +5;   % 进入s4

% 动作5: 停
P(2,2,5) = 1.0; R(2,5,2) = -10;    % 停留

% ========== 状态s3 (索引3) ==========
% 动作1: 上
P(3,1,1) = 0.7; R(3,1,1) = 0;    % 进入s1
P(3,4,1) = 0.3; R(3,1,4) = +5;   % 进入s4

% 动作2: 下
P(3,3,2) = 1.0; R(3,2,3) = -10;  % 边界惩罚

% 动作3: 左
P(3,3,3) = 1.0; R(3,3,3) = -10;  % 边界惩罚

% 动作4: 右
P(3,4,4) = 0.7; R(3,4,4) = +5;   % 进入s4
P(3,3,4) = 0.3; R(3,4,3) = -10;  % 边界惩罚

% 动作5: 停
P(3,3,5) = 1.0; R(3,5,3) = 0;    % 停留

% ========== 状态s4 (索引4) ==========
% 动作1: 上
P(4,2,1) = 0.7; R(4,1,2) = -10;  % 进入s2
P(4,4,1) = 0.3; R(4,1,4) = -10;  % 边界惩罚

% 动作2: 下
P(4,4,2) = 0.7; R(4,2,4) = -10;  % 边界惩罚
P(4,3,2) = 0.3; R(4,2,3) = 0;    % 进入s3

% 动作3: 左
P(4,3,3) = 0.7; R(4,3,3) = 0;    % 进入s3
P(4,2,3) = 0.3; R(4,3,2) = -10;  % 进入s2

% 动作4: 右
P(4,4,4) = 1.0; R(4,4,4) = -10;  % 边界惩罚

% 动作5: 停
P(4,4,5) = 1.0; R(4,5,4) = 50;    % 停留

%% 3. 定义策略（均匀随机策略）
pi = zeros(n_states, n_actions);
pi(:,:) = 0.2;  % 每个状态选择每个动作的概率都是0.2

%% 4. 计算策略下的转移矩阵P_pi和期望奖励向量R_pi（修正）
P_pi = zeros(n_states, n_states);
R_pi = zeros(n_states, 1);

for s = 1:n_states %[output:group:7921c04c]
    total_prob = 0;  % 用于验证概率和为1
    for a = 1:n_actions
        action_prob = pi(s, a);
        for s_next = 1:n_states
            if P(s, s_next, a) > 0
                P_pi(s, s_next) = P_pi(s, s_next) + action_prob * P(s, s_next, a);
                % 期望奖励：动作概率 × 转移概率 × 即时奖励
                R_pi(s) = R_pi(s) + action_prob * P(s, s_next, a) * R(s, a, s_next);
                total_prob = total_prob + action_prob * P(s, s_next, a);
            end
        end
    end
    fprintf('状态s%d: 总概率 = %.6f\n', s, total_prob); %[output:9cc7fe3a]
end %[output:group:7921c04c]

disp('策略下的转移矩阵 P_pi:'); %[output:9ed829cb]
disp(P_pi); %[output:5e30f4e0]

disp('策略下的期望奖励向量 R_pi:'); %[output:15955f49]
disp(R_pi); %[output:63c1f092]

% 验证每行和为1
disp('验证: P_pi每行和应为1'); %[output:54027422]
disp(sum(P_pi, 2)); %[output:8613914f]

%% 5. 解析解（真值）使用矩阵公式
I = eye(n_states);
V_true_matrix = (I - gamma * P_pi) \ R_pi;

fprintf('\n========== 矩阵形式解析解 ==========\n'); %[output:6e37e4db]
fprintf('V*(s1) = %8.4f\n', V_true_matrix(1)); %[output:3a141907]
fprintf('V*(s2) = %8.4f\n', V_true_matrix(2)); %[output:16344908]
fprintf('V*(s3) = %8.4f\n', V_true_matrix(3)); %[output:8c6a5e30]
fprintf('V*(s4) = %8.4f\n', V_true_matrix(4)); %[output:510df009]

%% 6. 贝尔曼自举（迭代策略评估）
max_iter = 1000;
tolerance = 1e-10;
V_iter = zeros(n_states, 1);  % 初始值函数
V_history_iter = zeros(n_states, max_iter+1);
V_history_iter(:, 1) = V_iter;

fprintf('\n========== 贝尔曼自举 (迭代策略评估) ==========\n'); %[output:8b0bedcb]
for iter = 1:max_iter %[output:group:4eb78798]
    V_old = V_iter;
    
    % 贝尔曼更新：V_{k+1} = R_pi + gamma * P_pi * V_k
    V_iter = R_pi + gamma * P_pi * V_old;
    
    V_history_iter(:, iter+1) = V_iter;
    
    % 检查收敛
    if norm(V_iter - V_old, inf) < tolerance
        fprintf('迭代 %d 次后收敛\n', iter); %[output:21a9752e]
        V_history_iter = V_history_iter(:, 1:iter+1);
        break;
    end
    
    if iter == max_iter
        fprintf('达到最大迭代次数 %d\n', max_iter);
    end
end %[output:group:4eb78798]

fprintf('最终迭代值: V = [%8.4f, %8.4f, %8.4f, %8.4f]\n', ... %[output:group:3869929e] %[output:89d6ffc5]
        V_iter(1), V_iter(2), V_iter(3), V_iter(4)); %[output:group:3869929e] %[output:89d6ffc5]
fprintf('与矩阵解析解误差: %.6e\n', norm(V_iter - V_true_matrix)); %[output:1fb66de7]

%% 7. 矩阵形式TD(0)迭代
alpha = 0.5;  % 学习率
n_iterations = 100;  % 迭代次数
V_td = zeros(n_states, 1);  % 初始值全为0
V_history_td = zeros(n_states, n_iterations+1);
V_history_td(:, 1) = V_td;

fprintf('\n========== 矩阵形式TD(0)迭代 ==========\n'); %[output:76ae3a2e]
fprintf('学习率 α = %.2f, 折扣因子 γ = %.2f\n\n', alpha, gamma); %[output:9a8242ce]

for iter = 1:n_iterations %[output:group:02c3893c]
    % TD(0)矩阵更新公式: V <- V + α[R_pi + (γP_pi - I)V]
    TD_target = R_pi + gamma * P_pi * V_td;
    TD_error = TD_target - V_td;
    V_td = V_td + alpha * TD_error;
    
    V_history_td(:, iter+1) = V_td;
    
    % 每10次迭代显示一次
    if mod(iter, 10) == 0
        fprintf('迭代 %3d: V = [%7.3f, %7.3f, %7.3f, %7.3f]\n', ... %[output:15c32b68]
                iter, V_td(1), V_td(2), V_td(3), V_td(4)); %[output:15c32b68]
    end
end %[output:group:02c3893c]



