%% 8. 基于采样的TD(0)算法（蒙特卡洛方法）
fprintf('\n========== 基于采样的TD(0)算法 ==========\n'); %[output:5a4e9d24]

% 采样参数
n_episodes = 500;      % 采样轮数（episodes）
max_steps_per_episode = 1000;  % 每个episode最大步数
alpha_sample = 0.2;     % 学习率（较小以避免震荡）
V_sample = zeros(n_states, 1);  % 初始化值函数
V_history_sample = zeros(n_states, n_episodes+1);
V_history_sample(:, 1) = V_sample;

% 记录每个状态被访问的次数（用于适应性学习率）
state_visit_count = zeros(n_states, 1);

% 存储每个episode的轨迹
trajectories = cell(n_episodes, 1);

fprintf('开始基于采样的TD(0)学习...\n'); %[output:9f709acc]
fprintf('总episodes数: %d, 最大步数/episode: %d, 学习率: %.3f\n\n', ... %[output:group:737997dc] %[output:20e131a9]
        n_episodes, max_steps_per_episode, alpha_sample); %[output:group:737997dc] %[output:20e131a9]

for episode = 1:n_episodes %[output:group:10032459]
    % 随机选择起始状态（均匀分布）
    current_state = randi(n_states);
    
    % 记录当前episode的轨迹
    trajectory = struct();
    trajectory.states = [];
    trajectory.actions = [];
    trajectory.rewards = [];
    trajectory.next_states = [];
    
    step_count = 0;
    
    while step_count < max_steps_per_episode
        % 记录状态访问次数
        state_visit_count(current_state) = state_visit_count(current_state) + 1;
        
        % 根据策略选择动作
        action_probs = pi(current_state, :);
        action = randsample(n_actions, 1, true, action_probs);
        
        % 根据转移概率确定下一个状态
        next_state_probs = squeeze(P(current_state, :, action));
        next_state = randsample(n_states, 1, true, next_state_probs);
        
        % 获取即时奖励
        reward = R(current_state, action, next_state);
        
        % 记录轨迹
        trajectory.states(end+1) = current_state;
        trajectory.actions(end+1) = action;
        trajectory.rewards(end+1) = reward;
        trajectory.next_states(end+1) = next_state;
        
        % TD(0)更新：V(s) ← V(s) + α[r + γV(s') - V(s)]
        td_target = reward + gamma * V_sample(next_state);
        td_error = td_target - V_sample(current_state);
        
        % 适应性学习率（随时间递减）
        adaptive_alpha = alpha_sample / sqrt(state_visit_count(current_state));
        V_sample(current_state) = V_sample(current_state) + adaptive_alpha * td_error;
        
        % 转移到下一个状态
        current_state = next_state;
        step_count = step_count + 1;
        
        % 可以添加终止条件（如果有终止状态）
        % 这里假设没有终止状态，所以一直运行直到达到最大步数
    end
    
    % 存储轨迹
    trajectories{episode} = trajectory;
    
    % 记录当前值函数
    V_history_sample(:, episode+1) = V_sample;
    
    % 每500个episode显示一次进度
    if mod(episode, 500) == 0
        fprintf('Episode %5d: V = [%7.3f, %7.3f, %7.3f, %7.3f]\n', ... %[output:71a7d20b]
                episode, V_sample(1), V_sample(2), V_sample(3), V_sample(4)); %[output:71a7d20b]
    end
end %[output:group:10032459]

fprintf('\n采样TD(0)完成！\n'); %[output:67bb53bb]
fprintf('最终值: V = [%8.4f, %8.4f, %8.4f, %8.4f]\n', ... %[output:group:0dd15a9f] %[output:3a0d64ef]
