% main.m - Sarsa Grid World Runner
clear; close all; clc;

%% 1. 初始化参数 (与原始代码一致)
agent_state = [1, 1];
final_state = [3, 4];
obstacle_state = [2,2; 3,2; 3,3; 2,4; 4,4; 2,5];
x_length = 5;
y_length = 5;

% 训练参数
episode_count = 5000;
max_steps = 1000;
alpha = 0.1;
gamma = 0.9;
epsilon = 0.1;

%% 2. 实例化对象
env = GridWorld(x_length, y_length, agent_state, final_state, obstacle_state);
% Action space size is 5 (Up, Right, Down, Left, Stay)
agent = SarsaAgent(env.State_Space_Size, 5, alpha, gamma, epsilon);

fprintf('开始训练...\n'); %[output:37ebdc29]
tic;

%% 3. 训练循环
for ep = 1:episode_count
    % 重置到起点
    curr_s_idx = env.coord2idx(env.Start_State);
    
    % Sarsa: 先选择动作 a
    curr_a_idx = agent.choose_action(curr_s_idx);
    
    for step = 1:max_steps
        % 1. 执行动作 (Environment Step)
        [next_s_idx, reward, is_done] = env.step(curr_s_idx, curr_a_idx);
        
        if is_done
            % 2a. 终点更新 (无 next action)
            agent.update_terminal(curr_s_idx, curr_a_idx, reward);
            break;
        else
            % 2b. 选择下一个动作 a' (Sarsa logic)
            next_a_idx = agent.choose_action(next_s_idx);
            
            % 3. 更新 Q 表
            agent.update(curr_s_idx, curr_a_idx, reward, next_s_idx, next_a_idx);
            
            % 4. 状态转移
            curr_s_idx = next_s_idx;
            curr_a_idx = next_a_idx;
        end
    end
end
toc; %[output:5b605288]

%% 4. 结果展示
[V_values, optimal_policy] = agent.get_results();

% 调用重构后的可视化方法
env.plot_values(V_values); %[output:9186a8b4]
env.plot_policy(optimal_policy); %[output:63d3b582]

% 演示一条最优路径
fprintf('演示最优路径...\n'); %[output:09298fff]
test_state = env.coord2idx(env.Start_State);
path_history = test_state;
for i = 1:20
    % 纯贪婪选择
    [~, best_a] = max(agent.Q_Table(test_state, :));
    [next_s, ~, done] = env.step(test_state, best_a);
    test_state = next_s;
    path_history(end+1) = test_state;
    if done, break; end
end
env.plot_trajectory(path_history); %[output:7d47fb44]

% 演示一条最优路径
fprintf('演示最优路径...\n'); %[output:71e07736]
test_state = env.coord2idx(env.Start_State);
path_history = test_state;

for i = 1:50  % 稍微增加步数上限以防绕路
    [~, best_a] = max(agent.Q_Table(test_state, :));
    [next_s, ~, done] = env.step(test_state, best_a);
    test_state = next_s;
    path_history(end+1) = test_state;
    if done, break; end
end

% 2. 绘制带随机噪声的轨迹 (新功能)
% 如果多次运行 episode 并把所有 history 拼起来，这个图会更壮观
env.plot_stochastic_trajectory(path_history); %[output:4139b145]

%%
%% demo_spectacular.m - 展示群体探索效果
clear; close all; clc;

% 1. 初始化
x_len = 5; y_len = 5;
start = [1, 1]; final = [3, 4];
obs = [2,2; 3,2; 3,3; 2,4; 4,4; 2,5];

env = GridWorld(x_len, y_len, start, final, obs);
agent = SarsaAgent(env.State_Space_Size, 5, 0.1, 0.9, 0.5); % 初始 epsilon = 0.5 (高探索)

% 用于存储所有的路径历史
all_histories = {};

% 2. 训练并收集数据
episodes = 300; % 跑300个回合，足够画出壮观的图
fprintf('正在收集 %d 个回合的路径数据...\n', episodes); %[output:151d6d31]

for ep = 1:episodes
    curr_s = env.coord2idx(env.Start_State);
    curr_a = agent.choose_action(curr_s);
    
    path = [curr_s]; % 记录当前回合的路径
    
    for step = 1:100
        [next_s, r, done] = env.step(curr_s, curr_a);
        
        if done
            agent.update_terminal(curr_s, curr_a, r);
            path(end+1) = next_s;
            break;
        else
            next_a = agent.choose_action(next_s);
            agent.update(curr_s, curr_a, r, next_s, next_a);
            
            curr_s = next_s;
            curr_a = next_a;
            path(end+1) = curr_s;
        end
    end
    
    % 保存这一条路径到大集合中
    all_histories{end+1} = path;
    
    % 慢慢降低探索率，模拟从"混乱"到"有序"的过程
    agent.decay_epsilon();
end

fprintf('收集完毕，开始绘图...\n'); %[output:9f340adf]

%% 3. 绘制壮观的聚合路径图
% 这将画出所有300条路径的叠加
% 半透明的绿色线条叠加在一起，访问越多的地方越亮（像热力图）
env.plot_stochastic_trajectory(all_histories); %[output:35ed1f17]


